//       }}
//     </StoreContext.Provider>
//   );
// };

const WithStoreService = (Wrapped) => {
  const storeService = new StoreService();

  return class extends React.Component {
    render() {
      return (
        <StoreContext.Provider value={storeService}>
          <Wrapped {...this.props} />
        </StoreContext.Provider>
      );
    }
  };
};
export default WithStoreService;
