import axios from 'axios';
import { getProductsUrl, signInUrl, getCategories } from '../config/config.json';
import { Product, Token, SignIn } from '../interfaces';

// fetch('https://fakestoreapi.com/products/categories')
//   .then(res => res.json())
//   .then(json => console.log(json))
export default class StoreService {

  getCategories = async () => {
    const { data } = await axios.get<String[]>(getCategories);
    return data;
  };
  getCategory = async (category: any) => {
    const { data } = await axios.get<Product[]>(`${getProductsUrl}/${category}`)
    return data
  }
  getProducts = async () => {
    const { data } = await axios.get<Product[]>(getProductsUrl);
    return data;
  };
  getProduct = async (id: number) => {
    const { data } = await axios.get<Product>(`${getProductsUrl}/${id}`);
    return data;
  };

  signIn = async (data: any) => {
    const response = await axios.post<Token>(signInUrl, data);
    const token = response?.data?.token;
    localStorage.setItem('token', token);
    return token
  }
}
