import axios from "axios";
import { useMutation, useQuery } from "react-query";
import { WithStoreService } from "../components/hoc";
import StoreService from "../services/store-service";
import { signInUrl} from "../config/config.json"


interface StoreListProps {
  storeService: any
}
const storeService = new StoreService();

export const useProducts = () => useQuery(['products'], storeService.getProducts)
export const useSignIn = (values: any) => useMutation(newValues => {
  return axios.post(signInUrl, newValues)
})
