import { Product } from '../interfaces';
import { useQuery, QueryClient, UseQueryOptions } from "react-query";
import StoreService from "../services/store-service";


const storeService = new StoreService();

// const queryClient = new QueryClient() as any




export const useCategories = () => useQuery<UseQueryOptions>(['categories'], storeService.getCategories);
export const useCategory = (category: string) => useQuery(['categories', category], () => storeService.getCategory(category));


