// export default WithStoreService;

const WithStoreService = (Component) => {
  const x = useProducts();
  return ({ ...props }) => {
    return (
      <StoreContext.Provider value={x}>
        {() => {
          return <Component {...props} />;
        }}
      </StoreContext.Provider>
    );
  };
};

export default WithStoreService;
